import React, { Component } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './slider.scss';
import { SlImg } from './components';
import { NavLink } from 'react-router-dom';
import CN from 'classnames';

const StaLand = (props) => (
	<section className={props.container}>
		<article className={props.article}>
			<div className="aspect-ratio aspect-ratio--7x5">
				<img src={props.src} className="whale z-max" alt="img" />
			</div>
		</article>
		<div className="db mt2 mt1-m">
			<p className="optima f3 f4-m black fw5 pb2 pb1-m">{props.title}</p>
			<p className="optima f6 silver fw4 ttu">{props.group}</p>
		</div>
	</section>
);
const LandE = (props) => (
	<div className="dim">
		<article className={props.article}>
			<div className="aspect-ratio aspect-ratio--7x5">
				<div
					className="aspect-ratio--object cover"
					style={{
						background: `url(${props.background}) no-repeat top center`,
					}}
				/>
			</div>
		</article>
		<a
			href={props.to}
			className="db mt2 mt1-m"
			noopener
			noreferrer
			target="blank"
		>
			<div>
				<p className={CN('optima f3 f4-m black fw5 pb2 pb1-m', props.hover)}>
					{props.title}
				</p>
				<p className={CN('optima f6 silver fw4 ttu', props.hover)}>{props.group}</p>
			</div>
		</a>
	</div>
);

export default class Lima extends Component {
	render() {
		var settings = {
			dots: true,
			fade: true,
			infinite: true,
			speed: 500,
			autoplay: false,
			slidesToShow: 1,
			slidesToScroll: 1,
			className: 'slides',
			arrows: true,
		};
		return (
			<section className="w-70 w-100-m center ">
				<Slider {...settings}>
					<LandE
						to="https://www.ideascudo.com/homepage/"
						article="bg-light-gray"
						background="./img/ideascudo/ideascudo_1.png"
						title="Ideascudo"
						group="logo"
					/>
					<StaLand
						container="w-100-m mb4"
						src="./img/lima/3.png"
						title="Ideascudo"
						group="logo"
					/>
					<StaLand
						container="w-100-m mb4"
						src="./img/lima/1.png"
						title="Ideascudo"
						group="logo"
					/>
					<StaLand
						container="w-100-m mb4"
						src="./img/lima/2.png"
						title="Ideascudo"
						group="logo"
					/>
				</Slider>
			</section>
		);
	}
}

